var DIA_INICIO = 6
//rectifica posición primer día en cada mes
var RECTIF = Array(0,3,3,6,1,4,6,2,5,0,3,5);
//nombres de meses
var NOMBRES_MES = Array("Enero","Febrero","Marzo","Abril","Mayo","Junio","Julio","Agosto",
