//días en cada mes
var DIAS_MES = Array(31,28,31,30,31,30,31,31,30,31,30,31);
//El primer día es el lunes, con índice 0
var NOMBRES_DIAS_SEMANA = Array("Lunes", "Martes", "Miércoles", "Jueves", "Viernes",
